
CREATE TABLE `pdv` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `Address` geometry NOT NULL,
  `CoverageArea` geometry NOT NULL,
  `Document` varchar(255) NOT NULL,
  `OwnerName` varchar(255) NOT NULL,
  `TradingName` varchar(255) NOT NULL,
  PRIMARY KEY (`Id`),
  UNIQUE (`Document` )
) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;



UPDATE pdv SET coverageArea = ST_GeomFromText(ST_AsText(coverageArea), 4326);
UPDATE pdv SET Address = ST_GeomFromText(ST_AsText(Address), 4326);



